import React, {useEffect} from "react";
import { Container, Form } from "semantic-ui-react";
import Event from "./Event";




const aboutStyle = {
    marginTop: `20em`,
    fonstSize: `6em`
}

const listStyle = {
    marginTop: `4em`,
    fontSize: `1.8em`
    }



function About () {

    let events = []

    useEffect (() => {
        fetch("http://localhost:3000/events")
            .then(response => response.json())
         .then(data => events = data)
     }, []);


    return (
        <div>
        <Container className="ui center aligned container" style={aboutStyle} >
        <div className=" ui huge header">Events:</div>
        <div style={listStyle} class="ui list">
            {events.map((event) => (
                <Event date={event.date} start={event.start} end={event.end} description={event.description} title={event.title}/>
            ))}
        </div>
        </Container>
        </div>
    );
}

export default About;
